def findwalls(maze):
    walls = []
    for idx_y, row in enumerate(maze):
        for idx_x, cell in enumerate(row):
            if cell == 1:
                walls.insert(0,(idx_x,idx_y))
    #vizwalls(walls, maze)
    return walls
    

def traildir(trail, maze):
    for element in trail:
        try:
            maze [element[1]][element[0]] = 3
        except IndexError:
            print("indexerror" + str(element))
            return False
    visualize(maze)    
        
def vizwalls(walls, maze):
    for element in walls:
        try:
            maze [element[1]][element[0]] = 4
        except IndexError:
            print("indexerror" + str(element))
            return False
    visualize(maze)    
    
def main(maze):
    global start
    global end
    global size
    start = tuple(start)
    end = tuple(end)
    width = int(size[0])
    height = int(size[1])
    a = AStar()
    walls = findwalls(maze)
    a.init_grid(width, height, walls, start, end)
    path, trail = a.solve()
    print(path) 

def main_visual(maze):
    global start
    global end
    global size
    start = tuple(start)
    end = tuple(end)
    width = int(size[0])
    height = int(size[1])
    a = AStar()
    visualize(maze)
    walls = findwalls(maze)
    a.init_grid(width, height, walls, start, end)
    path, trail = a.solve()
    traildir(trail, maze)
    print('path: ' + path) 
    #print(trail)
    
